import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { City } from '../models/city.model';
import { environment } from '../../environments/environment';


@Injectable({
  providedIn: 'root'
})
export class CityService {

  baseApiUrl: string = environment.baseApiUrl;

  constructor(private http: HttpClient) { }

  getCity(id: number): Observable<any> {
    return this.http.get(`${this.baseApiUrl}/api/Cities/${id}`);
  }


  //https://localhost:7043/api/City/1

  getCitiesByCountryId(id: number): Observable<any> {
    return this.http.get(`${this.baseApiUrl}/api/City/${id}`);
  }


  createCity(city: City): Observable<any> {
    return this.http.post(`${this.baseApiUrl}/api/Cities`, city);
  }

  updateCity(id: number, city: City): Observable<any> {
    return this.http.put(`${this.baseApiUrl}/api/Cities/${id}`, city);
  }

  deleteCity(id: number): Observable<any> {
    return this.http.delete(`${this.baseApiUrl}/api/Cities/${id}`, { responseType: 'text' });
  }
}
