import { HttpClient } from '@angular/common/http';
import { Component, OnInit, ViewChild } from '@angular/core';
import { count, Observable } from 'rxjs';
import { Person } from 'src/app/models/person.model';
import { environment } from '../../../../environments/environment';
import { City } from '../../../models/city.model';
import { Country } from '../../../models/country.model';
import { CityService } from '../../../services/city.service';
import { CountryService } from '../../../services/country.service';
import { PeopleService } from '../../../services/people.service';

import { MatDialog, MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { ConfirmationDialogComponent } from '../../confirmation-dialog/confirmation-dialog.component';


@Component({
  selector: 'app-people-list',
  templateUrl: './people-list.component.html',
  styleUrls: ['./people-list.component.css']
})
export class PeopleListComponent {
  id: number = 0;
  countries: Country[] = [];
  cities: City[] = [];
  resumeFileName: string = "";
  people: Person[] = [

  ];

  baseApiUrl: string = environment.baseApiUrl;
  resumeDownloadUrl: string = environment.resumeBaseUrl;

  constructor(
    private http: HttpClient,
    private peopleService: PeopleService,
    private countryService: CountryService,
    private cityService: CityService,
    private dialog: MatDialog
  ) { }


  ngOnInit(): void {
    this.peopleService.getAllPeople()
      .subscribe({
        next: (people) => {
          this.people = people;
        },
        error: (response) => {
          console.log(response);
        }
      });

    this.countryService.getCountries()
      .subscribe({
        next: (countries) => {
          this.countries = countries;
          console.log(countries);
        },
        error: (response) => {
          console.log(response);
        }
      });

    this.cityService.getCities()
      .subscribe({
        next: (cities) => {
          this.cities = cities;
          console.log(cities);
        },
        error: (response) => {
          console.log(response);
        }
      });
  }


  getCountryName(countryId: any) {
    const country = this.countries.find((c: { id: any; }) => c.id === countryId);
    return country ? country.name : '';
  }

  getCityName(cityId: any) {
    const city = this.cities.find((c: { id: any; }) => c.id === cityId);
    return city ? city.name : '';
  }
  
  getResumeFile(resumeFileName: string) {
    return this.resumeDownloadUrl + '?fileName=' + resumeFileName;
  }


  //deleteById(id: number): Observable<Person> {
  //  if (confirm("Are you sure you want to delete this person?")) {
  //    this.peopleService.deletePerson(id);
  //    this.peopleService.getAllPeople().subscribe(data => {
  //      this.people = data;
  //    });
  //    return this.http.delete<Person>(this.baseApiUrl + '/api/people/' + id);
  //  }
  //  return this.http.delete<Person>(this.baseApiUrl + '/api/people/' + id);
  //}


  @ViewChild(MatSort) sort: MatSort;

  //constructor(private dialog: MatDialog, private peopleService: PeopleService) { }

  openDeleteConfirmationDialog(person: Person) {
    const dialogRef = this.dialog.open(ConfirmationDialogComponent, {
      width: '350px',
      data: {
        name: person.name,
        country: person.countryId,
        city: person.cityId
      }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.deleteById(person.id);
      }
    });
  }

  deleteById(id: number) {
    this.peopleService.deletePerson(id).subscribe(() => {
      this.peopleService.getAllPeople().subscribe(people => {
        this.people = people;
      });
    });
  }

}
<div>
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.0/css/bootstrap.min.css">
  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.1/jquery.min.js"></script>
  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.0/js/bootstrap.min.js"></script>
</div>

<div class="container my-5">
  <h1 class="mb-3">Personal Information</h1>

  <table class="table" *ngIf="people && people.length > 0">
    <thead>
      <tr>
        <th class="text-center">#</th>
        <th>Name</th>
        <th>Country</th>
        <th>City</th>
        <th>Language Skills</th>
        <th>Resume</th>
        <th>Date Of Birth</th>
        <th class="text-center">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let person of people">
        <td> {{person.id}} </td>
        <td> {{person.name}} </td>
        <!--<td> {{person.countryId}} </td>
        <td> {{person.cityId}} </td>-->
        <td> {{getCountryName(person.countryId)}} </td>
        <td> {{getCityName(person.cityId)}} </td>
        <td> {{person.languageSkills}} </td>
        <!--<td> {{person.resumeFileName}} </td>-->
        <td>
          <a href="{{getResumeFile(person.resumeFileName)}}">
            {{person.resumeFileName}}
            <span class="glyphicon glyphicon-download-alt"></span>
          </a>
        </td>
        <td> {{person.dateOfBirth | date: 'dd MMM yyyy'}} </td>
        <td>
          <a [routerLink]="['/people', 'edit', person.id]" class="download-icon-view">
            <span class="glyphicon glyphicon-eye-open"></span>
          </a>
          <a [routerLink]="['/people', 'edit', person.id]" class="download-icon-edit">
            <span class="	glyphicon glyphicon-pencil"></span>
          </a>
          <a class="download-icon-remove">
            <span class="glyphicon glyphicon-remove"></span>
          </a>
        </td>

      </tr>
    </tbody>
  </table>
  <p *ngIf="!people || people.length <= 0">No Person Found</p>
</div>
