import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { City } from '../../models/city.model';
import { Country } from '../../models/country.model';
import { Person } from '../../models/person.model';
import { CityService } from '../../services/city.service';
import { CountryService } from '../../services/country.service';
import { PeopleService } from '../../services/people.service';

@Component({
  selector: 'app-add-person',
  templateUrl: './add-person.component.html',
  styleUrls: ['./add-person.component.css']
})
export class AddPersonComponent {
  countryId: number = 1;
  countries!: Country[];
  cities!: City[];
  /*  addPersonRequest: PersonRequest = {};*/

  addPersonRequest: Person = {
    id: '',
    name: '',
    country: '',
    city: '',
    languageSkills: '',
    dateOfBirth: new Date(2023, 1, 1),
    resumeFileName: '',
    languages: []
  };

  constructor(private personService: PeopleService, private router: Router, private countryService: CountryService, private cityService: CityService) { }


  ngOnInit() {
    this.countryService.getCountries().subscribe((data: Country[]) => {
      this.countries = data;
    });
  }

  //loadCities() {
  //  this.cityService.getCitiesByCountry(this.addPersonRequest.countryId).subscribe((data: City[]) => {
  //    this.cities = data;
  //  });
  //}

  loadCities() {
    this.cityService.getCitiesByCountryId(this.countryId).subscribe((data: City[]) => {
      this.cities = data;
    });
  }


  addPerson() {
    //console.log(this.addPersonRequest);
    this.personService.addPerson(this.addPersonRequest)
      .subscribe({
        next: (person) => {
          //console.log(person);
          this.router.navigate(['people']);

        }
      });
  }
}
