import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Country } from '../models/country.model';


@Injectable({
  providedIn: 'root'
})
export class CountryService {

  private baseUrl = 'http://localhost:7043/api/Countries';

  constructor(private http: HttpClient) { }

  getCountries(): Observable<any> {
    return this.http.get(`${this.baseUrl}`);
  }

  getCountry(id: number): Observable<any> {
    return this.http.get(`${this.baseUrl}/${id}`);
  }

  createCountry(country: Country): Observable<any> {
    return this.http.post(`${this.baseUrl}`, country);
  }

  updateCountry(id: number, country: Country): Observable<any> {
    return this.http.put(`${this.baseUrl}/${id}`, country);
  }

  deleteCountry(id: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/${id}`, { responseType: 'text' });
  }
}
