import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { City } from '../../models/city.model';
import { Country } from '../../models/country.model';
import { Language } from '../../models/language.model';
import { Person } from '../../models/person.model';
import { CityService } from '../../services/city.service';
import { CountryService } from '../../services/country.service';
import { LanguageService } from '../../services/language.service';
import { PeopleService } from '../../services/people.service';

import { HttpClient, HttpEvent, HttpEventType, HttpHeaders, HttpRequest } from '@angular/common/http';
import { FormBuilder, FormGroup, NgForm } from '@angular/forms';


import { FileUploadService } from '../../services/file-upload.service';
import { HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../../environments/environment';

@Component({
  selector: 'app-add-person',
  templateUrl: './add-person.component.html',
  styleUrls: ['./add-person.component.css']
})
export class AddPersonComponent implements OnInit {

  person: Person = {
    id: 0,
    name: '',
    countryId: 0,
    cityId: 0,
    languageSkills: '',
    dateOfBirth: new Date(),
    resumeFileName: '',
    languages: [],
    resumeFile: new File([], '')

  };
  selectedFile: File = new File([], '');
  success = false;
  error = false;
  errorMessage: string = "";


  languageSkills: string = "";
  countries!: Country[];
  cities!: City[];
  isChecked: boolean = false;
  languages: Language[] = [];


  addPersonForm!: FormGroup;
  selectedResumeFile!: File;

  addPersonRequest: Person = {
    id: 0,
    name: '',
    countryId: 0,
    cityId: 0,
    languageSkills: '',
    dateOfBirth: new Date(),
    resumeFileName: '',
    languages: [],
    resumeFile: new File([], '')

  };

  //constructor(
  //  private fb: FormBuilder,
  //  private personService: PeopleService,
  //  private router: Router,
  //  private countryService: CountryService,
  //  private cityService: CityService,
  //  private languageService: LanguageService
  //) {
  //  this.addPersonForm = this.fb.group({
  //    name: ['', Validators.required],
  //    country: ['', Validators.required],
  //    city: ['', Validators.required],
  //    languageSkills: ['', Validators.required],
  //    dateOfBirth: ['', Validators.required],
  //    resumeFileName: ['', Validators.required],
  //    languages: ['', Validators.required],
  //    cityId: ['', Validators.required],
  //    countryId: ['', Validators.required],
  //    resumeFile: ['', Validators.required],
  //  });
  //}

  baseApiUrl: string = environment.baseApiUrl;
  uploadProgress: number = 0;
  uploadSuccess: boolean = false;
  uploadMessage: string = "";
    err: string = "";

  constructor(
    private fb: FormBuilder,
    private personService: PeopleService,
    private router: Router,
    private countryService: CountryService,
    private cityService: CityService,
    private languageService: LanguageService,
    private http: HttpClient,
    private fileUploadService: FileUploadService

  ) { }


  ngOnInit() {
    this.countryService.getCountries().subscribe((data: Country[]) => {
      this.countries = data;
    });

    this.cityService.getCities().subscribe((data: City[]) => {
      this.cities = data;
    });

    this.languageService.getLanguages().subscribe((data: Language[]) => {
      this.languages = data;
    });
    this.getLanguages();
    this.loadCities();
  }


  getLanguages() {
    this.languageService.getLanguages().subscribe((data: Language[]) => {
      this.languages = data;
    });
  }


  loadCities() {
    this.cities = [];
    this.addPersonRequest.cityId = -1;
    this.cityService.getCitiesByCountryId(this.addPersonRequest.countryId).subscribe((data: City[]) => {
      this.cities = data;
    });
  }


  getSelectedLanguageSkills(): string {
    let selectedLanguageIds = this.languages
      .filter(language => language.isChecked)
      .map(language => language.id)
      .join(',');
    return selectedLanguageIds;
  }


  onFileSelected(event: any) {
    this.selectedResumeFile = <File>event.target.files[0];
    this.selectedFile = this.selectedResumeFile;
    console.log(this.selectedFile);
    console.log("File Got");
    console.log(this.selectedFile.name);
  }

 
  
  //addPerson(addPersonRequest: Person): Observable<Person> {
  //  /*debugger;*/
  //  const formData = new FormData();

  //  addPersonRequest.languageSkills = this.getSelectedLanguageSkills();

  //  formData.append('city', addPersonRequest.city);
  //  formData.append('country', addPersonRequest.country);
  //  formData.append('languages', addPersonRequest.languages);

  //  formData.append('id', addPersonRequest.id.toString());
  //  formData.append('name', addPersonRequest.name);
  //  formData.append('countryId', addPersonRequest.countryId.toString());
  //  formData.append('cityId', addPersonRequest.cityId.toString());
  //  formData.append('languageSkills', addPersonRequest.languageSkills);
  //  formData.append('dateOfBirth', addPersonRequest.dateOfBirth.toString());

  //  formData.append('resumeFileName', this.selectedFile.name);
  //  formData.append('resumeFile', this.selectedFile);
  //  console.log(addPersonRequest);

  //  this.showValidation();

  //  let headers = new HttpHeaders();
  //  headers.append('Content-Type', 'multipart/form-data');
  //  return this.http.post<Person>(`${this.baseApiUrl}/api/people`, formData, {
  //    headers: headers,
  //    reportProgress: true,
  //  });
  //}

  onAddPerson() {
    //this.fileUploadService.uploadFile(this.selectedFile).subscribe(
    this.showValidation();
    this.fileUploadService.uploadFile(this.selectedResumeFile).subscribe(
      (event) => {
        if (event instanceof HttpResponse) {
          //this.person.resumeFileName = event.body;
          this.person.resumeFileName = this.selectedFile.name;
          this.addPerson(this.person).subscribe();
        }
      }
    );
  }


  addPerson(addPersonRequest: Person): Observable<Person> {
    this.person.languageSkills = this.getSelectedLanguageSkills();
    return this.http.post<Person>(this.baseApiUrl + '/api/people', {
      ...addPersonRequest,
      countryId: addPersonRequest.countryId,
      cityId: addPersonRequest.cityId
    });
  }



  showValidation() {
    this.err = "\n";
    if (this.addPersonRequest.name == "") this.err += "\nPlease, Provide your name ";
    if (this.addPersonRequest.countryId == 0) this.err += "\nPlease, Select your country ";
    if (this.addPersonRequest.cityId <= 0  ) this.err += "\nPlease, Select your city";
    //if (this.addPersonRequest.languageSkills.length < 1) this.err += "\nPlease, Choose at least 1 languange skill ";
    this.err += "";
    if (this.err == "" || this.err == "\n") {
      return;
    }
    confirm(this.err);
  }

}
