import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Country } from '../models/country.model';
import { environment } from '../../environments/environment';


@Injectable({
  providedIn: 'root'
})
export class CountryService {

  baseApiUrl: string = environment.baseApiUrl;

  constructor(private http: HttpClient) { }

  getCountries(): Observable<any> {
    return this.http.get(`${this.baseApiUrl}/api/Countries`);
  }

  getCountry(id: number): Observable<any> {
    return this.http.get(`${this.baseApiUrl}/api/Countries/${id}`);
  }

  createCountry(country: Country): Observable<any> {
    return this.http.post(`${this.baseApiUrl}/api/Countries`, country);
  }

  updateCountry(id: number, country: Country): Observable<any> {
    return this.http.put(`${this.baseApiUrl}/api/Countries/${id}`, country);
  }

  deleteCountry(id: number): Observable<any> {
    return this.http.delete(`${this.baseApiUrl}/api/Countries/${id}`, { responseType: 'text' });
  }
}
