  onAddPerson() {
    this.fileUploadService.uploadFile(this.selectedFile).subscribe(
      (event) => {
        if (event instanceof HttpResponse) {
          this.person.fileUrl = event.body;
          this.fileUploadService.addPerson(this.person).subscribe(
            () => {
              this.success = true;
            },
            (err) => {
              this.error = true;
              this.errorMessage = err.error.message;
            }
          );
        }
      }
    );
  }

