import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Person } from '../../models/person.model';
import { PeopleService } from '../../services/people.service';

@Component({
  selector: 'app-add-person',
  templateUrl: './add-person.component.html',
  styleUrls: ['./add-person.component.css']
})
export class AddPersonComponent {

    addPersonRequest: Person = {
      id: '',
      name: '',
      country: '',
      city: '',
      languageSkills: '',
      dateOfBirth: new Date(2023, 1, 1),
      resumeFileName: '',
      languages: []
  };

  constructor(private employeeService: PeopleService, private router: Router) { }

  ngOnInit(): void {

  }

  addPerson() {
    //console.log(this.addPersonRequest);
    this.employeeService.addPerson(this.addPersonRequest)
      .subscribe({
        next: (person) => {
          //console.log(person);
          this.router.navigate(['people']);

        }
      });
  }
}
<div class="container my-5">
  <h1 class="mb-3">Add New Person</h1>


  <div class="row">
    <div class="col-6">
      <form #form="ngForm" (ngSubmit)="addPerson()">
        <div class="mb-3">
          <label for="name" class="form-label">Name</label>
          <input type="text" class="form-control" id="name"
                 name="name" [(ngModel)]="addPersonRequest.name">
        </div>

        <div class="mb-3">
          <label for="country" class="form-label">Country</label>
          <input type="text" class="form-control" id="country"
                 name="country" [(ngModel)]="addPersonRequest.country">
        </div>

        <div class="mb-3">
          <label for="city" class="form-label">City</label>
          <input type="text" class="form-control" id="city"
                 name="city" [(ngModel)]="addPersonRequest.city">
        </div>


        <div class="mb-3">
          <label for="languageSkills" class="form-label">Language Skills</label>
          <input type="text" class="form-control" id="languageSkills"
                 name="languageSkills" [(ngModel)]="addPersonRequest.languageSkills">
        </div>


        <div class="mb-3">
          <label for="resumeFileName" class="form-label">Resume</label>
          <input type="file" class="form-control" id="resumeFileName"
                 name="resumeFileName" [(ngModel)]="addPersonRequest.resumeFileName">
        </div>

        <div class="mb-3">
          <label for="dateOfBirth" class="form-label">Date Of Birth</label>
          <input type="date" class="form-control" id="dateOfBirth"
                 name="dateOfBirth" [(ngModel)]="addPersonRequest.dateOfBirth">
        </div>


        <button type="submit" class="btn btn-primary">Save</button>

      </form>
    </div>
  </div>
</div>
