import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { City } from '../models/city.model';


@Injectable({
  providedIn: 'root'
})
export class CityService {

  private baseUrl = 'http://localhost:7043/api/cities';

  constructor(private http: HttpClient) { }

  getCity(id: number): Observable<any> {
    return this.http.get(`${this.baseUrl}/${id}`);
  }

  //getCitiesByCountryId(countryId: number): Observable<any> {
  //  return this.http.get(`${this.baseUrl}/byCountryId/${countryId}`);
  //}


  //'https://localhost:7043/api/Location/1/cities' 
  getCitiesByCountryId(id: number): Observable<any> {
    //return this.http.get(`${this.baseUrl}/country/${id}`);
    return this.http.get(`https://localhost:7043/api/Location/${id}/cities`);
  }

  //getCitiesByCountryId(id: string): Observable<any> {
  //  return this.http.get(`${this.baseUrl}/country/${id}`);
  //}

  createCity(city: City): Observable<any> {
    return this.http.post(`${this.baseUrl}`, city);
  }

  updateCity(id: number, city: City): Observable<any> {
    return this.http.put(`${this.baseUrl}/${id}`, city);
  }

  deleteCity(id: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/${id}`, { responseType: 'text' });
  }
}
